dict = {"name":"md omar sharif", "profession":"web developer","language interest":"python","framework":"django"}print(dict)#access value using keyx=dict['profession']print(x)#get methody =dict.get("framework")print("access value items using by get method :", y)#keysz= dict.keys()print("key how to work :",z)# valuesu = dict.values()print('find all key values:', u)#Adding itemdict['front end language']="java script"print('new add item:',dict)#any key or value updatedict.update({"profession":'software developer'})print('update this value:', dict)# pop method use first item removedict.pop('name')print(dict)